import matplotlib.pyplot as plt

data = ''
data_list = [int(char) for char in data]

plt.figure(figsize=(9, 4))

for i, value in enumerate(data_list):
    x = i // 20 + 1
    y = i % 20 + 1
    color = 'blue' if value == 0 else 'red'
    plt.scatter(x, y, color=color)

plt.xlim(0, 37)  # 36
plt.ylim(0, 21)  # 20
plt.yticks(range(0, 21, 5))

plt.title('Starlink Shell 2 Servers Distribution')
plt.xlabel('Planes')
plt.ylabel('Satellites per plane')

plt.show()
